import { Feather } from '@expo/vector-icons';
import React, { useState } from 'react';
import { StyleSheet, View, Text, SafeAreaView, TextInput, TouchableOpacity, ScrollView , Image, Alert} from 'react-native';
import DateTimePickerModal from "react-native-modal-datetime-picker";
import { RootStackScreenProps } from '../types';
import moment from 'moment';
import SelectDropdown from 'react-native-select-dropdown'
import MaskInput from 'react-native-mask-input';
import { useToast } from "react-native-toast-notifications";

export default function Home({ navigation }: RootStackScreenProps<'Home'>) {
    const toast = useToast();

    const onDelete = () => {
        Alert.alert(
            "Eliminar",
            "¿Estás seguro de eliminar este registro?",
            [
              {
                text: "Cancel",
                onPress: () => console.log("Cancel Pressed"),
                style: "cancel"
              },
              { text: "OK", onPress: () => console.log("OK Pressed") }
            ]
          );
    }

    return(
        <SafeAreaView style={styles.container}>

            <ScrollView style={styles.form}>
                <View style={styles.formGroup}>
                    <Text style={styles.formLabel}>Nombre</Text>
                    <Text style={styles.formInfo}>Eduardo</Text>
                </View>
                <View style={styles.formGroup}>
                    <Text style={styles.formLabel}>Apellido</Text>
                    <Text style={styles.formInfo}>Gonzalez</Text>
                </View>
                <View style={styles.formGroup}>
                    <Text style={styles.formLabel}>Teléfono</Text>
                    <Text style={styles.formInfo}>+56 9 9999 9999</Text>
                </View>
                <View style={styles.formGroup}>
                    <Text style={styles.formLabel}>Dirección</Text>
                    <Text style={styles.formInfo}>Av. Las Heras #123</Text>
                </View>
                <View style={styles.formGroup}>
                    <Text style={styles.formLabel}>Género</Text>
                    <Text style={styles.formInfo}>Masculino</Text>
                </View>
                <View style={styles.formGroup}>
                    <Text style={styles.formLabel}>Fecha de nacimiento</Text>
                    <Text style={styles.formInfo}>01-01-2000</Text>
                </View>
                <View style={styles.formGroup}>
                    <Text style={styles.formLabel}>Profesión</Text>
                    <Text style={styles.formInfo}>Ingeniero</Text>
                </View>

                <View style={styles.actionButtons}>
                    <TouchableOpacity style={styles.buttonEdit} onPress={() => navigation.navigate('Customer')}>
                        <Text style={styles.actionButtonText}>Editar</Text>
                    </TouchableOpacity>
                    <TouchableOpacity style={styles.buttonDelete} onPress={() => onDelete()}>
                        <Text style={styles.actionButtonText}>Eliminar</Text>
                    </TouchableOpacity>
                </View>
            </ScrollView>

        </SafeAreaView>
    );
}

const styles = StyleSheet.create({
    container: {
        backgroundColor: '#FFFFFF',
        padding: 20,
        height: '100%',
    },
    form: {
        padding: 20,
    },
    formGroup: {
        marginBottom: 20,
    },
    formLabel: {
        fontSize: 16,
        color: '#9BA0A5',
    },
    formInfo: {
        fontSize: 16,
        color: '#4A4A4A',
    },
    actionButtons: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        marginTop: 20,
    },
    buttonEdit: {
        backgroundColor: '#3498db',
        padding: 10,
        borderRadius: 5,
        width: '48%',
    },
    buttonDelete: {
        backgroundColor: '#e74c3c',
        padding: 10,
        borderRadius: 5,
        width: '48%',
    },
    actionButtonText: {
        color: '#FFFFFF',
        fontSize: 16,
        textAlign: 'center',
    },
});
